        //============================================================
        // class CResources.CResourceInfo
        //============================================================
        internal class CResourceInfo
        {
            internal string ResID = null;
            internal string ResValue = null;
            internal int ArgCount = 0;
        }

        //============================================================
        // class CResources.CResourceInfoComparer
        //============================================================
        internal class CResourceInfoComparer : IComparer<CResourceInfo>
        {
            public int Compare(CResourceInfo info1, CResourceInfo info2)
            {
                return String.Compare(info1.ResID, info2.ResID, true);
            }
        }

        //------------------------------------------------------------
        // CResources Fields and Properties
        //------------------------------------------------------------
        private static List<CResourceInfo> resInfoList = new List<CResourceInfo>();

        //------------------------------------------------------------
        // CResources.MakeResourceID
        //------------------------------------------------------------
        internal static void MakeResourceID()
        {
            resInfoList.Clear();
            Init();

            StringBuilder sb1 = new StringBuilder();
            StringBuilder sb2 = new StringBuilder();
            string code1, code2;

            foreach (CResourceInfo info in resInfoList)
            {
                sb1.AppendFormat("{0},\n", info.ResID);
                sb2.AppendFormat(
                    "AddInfo(ResNo.{0}, \"{0}\", {1});\n",
                    info.ResID,
                    info.ArgCount);
            }
            code1 = sb1.ToString();
            code2 = sb2.ToString();
        }

        //------------------------------------------------------------
        // CResources.MakeResourceDef
        //------------------------------------------------------------
        internal static void MakeResourceDef()
        {
            string file1 = @"uncs.resources1.txt";
            string file2 = @"uncs.resources2.txt";

            string resStr = null;
            StringBuilder sb1 = new StringBuilder();
            StringBuilder sb2 = new StringBuilder();

            Regex re1 = new Regex("\r\n", RegexOptions.Singleline);
            Regex re2 = new Regex("\r|\n", RegexOptions.Singleline);
            Regex re3 = new Regex("\t", RegexOptions.Singleline);

            resInfoList.Clear();
            Init();

            sb1.Length = 0;
            sb2.Length = 0;
            foreach (CResourceInfo info in resInfoList)
            {
                resStr = info.ResValue;
                resStr = re1.Replace(resStr, "\\n");
                resStr = re2.Replace(resStr, "\\n");
                resStr = re3.Replace(resStr, "\\t");

                sb1.AppendFormat("{0}\t{1}\n", info.ResID, resStr);
                sb2.AppendFormat("{0}={1}\n", info.ResID, resStr);
            }

            File.WriteAllText(file1, sb1.ToString());
            File.WriteAllText(file2, sb2.ToString());
        }

        //------------------------------------------------------------
        // CResources.AddInfo
        //------------------------------------------------------------
        internal static void AddInfo(string rid, string rval, int carg)
        {
            CResourceInfo info = new CResourceInfo();
            info.ResID = rid;
            info.ResValue = rval;
            info.ArgCount = carg;
            resInfoList.Add(info);
        }

